class DetectSquares {
    std::unordered_map<int, std::unordered_map<int, int>> row_map;
    std::unordered_map<int, std::unordered_map<int, int>> col_map;
public:
    DetectSquares() {

    }

    void add(vector<int> point) {
        row_map[point[0]][point[1]]++;
        col_map[point[1]][point[0]]++;
    }

    int count(vector<int> point) {
        int ans = 0;
        for (auto [col, cnt] : row_map[point[0]]) {
            if (col == point[1]) continue;
            for (auto [row, cnt1] : col_map[point[1]]) {
                if (row == point[0] || abs(row - point[0]) != abs(col - point[1])) continue;
                ans += (cnt * cnt1 * row_map[row][col]);
            }
        }

        return ans;
    }
};

