class DetectSquares {
 private:
  using point_t = vector<int>;

  class Hash {
   public:
    size_t operator()(const point_t &point) const noexcept {
      constexpr size_t magic = 0x9e3779b9;
      size_t ret = 0U;
      for (const int coordinate : point) {
        ret ^= magic + coordinate + (ret << 6) + (ret >> 2);
      }
      return ret;
    }
  };

 public:
  DetectSquares() {
  }

  void add(const vector<int> &point) {
    if (++point_to_count_[point] == 1) {
      x_to_ys_[point.front()].emplace_back(point.back());
    }
  }

  int count(const vector<int> &point) {
    const int x = point.front();
    const int y = point.back();
    auto itr_x = x_to_ys_.find(x);
    if (itr_x == x_to_ys_.end()) {
      return 0;
    }

    int ret = 0;
    for (const int another_y : itr_x->second) {
      if (another_y == y) {
        continue;
      }

      const int side_length = abs(another_y - y);
      ret += get_count({
        point_t{x, another_y},
        point_t{x - side_length, y},
        point_t{x - side_length, another_y}
      });
      ret += get_count({
        point_t{x, another_y},
        point_t{x + side_length, y},
        point_t{x + side_length, another_y}
      });
    }
    return ret;
  }

 private:
  int get_count(const vector<point_t> &points) {
    int ret = 1;
    for (const point_t &point : points) {
      auto itr = point_to_count_.find(point);
      if (itr == point_to_count_.end()) {
        ret = 0;
        break;
      }
      ret *= itr->second;
    }
    return ret;
  }

  unordered_map<int, vector<int>> x_to_ys_;
  unordered_map<point_t, int, Hash> point_to_count_;
};
```
