        m[{point[0],point[1]}]++;
    }

    int count(vector<int> point) {
        int x1 = point[0];
        int y1 = point[1];
        int ans=0;
        for(auto &i:m)
        {
            int x2 = i.first.first;
            int y2 = i.first.second;
            int a2 = m[{x2,y2}];
            if(y2==y1)
            {
                int len = abs(x1-x2);
                int cntp1=0,cntp2=0,cntn1=0,cntn2=0;
                for(auto &j:m)
                {
                    int x3 = j.first.first;
                    int y3 = j.first.second;
                    if(x3==x1 && y3>y1 && y3-y1==len)
                    {
                        cntp1+=m[{x3,y3}];
                    }
                    if(x3==x2 && y3>y1 && y3-y1==len)
                    {
                        cntp2+=m[{x3,y3}];
                    }
                }
                ans+=cntp1*cntp2*a2;
                for(auto &j:m)
