    const x_col_count = new Map();
    return {
        add([x, y]) {
            if (!x_col_count.has(x)) {
                x_col_count.set(x, new Map());
            }
            const qxcol = x_col_count.get(x);
            qxcol.set(y, (qxcol.get(y) || 0) + 1);
        },
        count([qx, qy]) {
            const qxcol = x_col_count.get(qx);
            if (!qxcol) return 0;

            let ans = 0;
            for (const a_y_on_qxcol of qxcol.keys()) {
                const side = Math.abs(qy - a_y_on_qxcol);
