class DetectSquares:

    def __init__(self):
        self.points = defaultdict(int)

    def add(self, point: List[int]) -> None:
        self.points[tuple(point)] += 1

    def count(self, point: List[int]) -> int:

        px,py = point

        x_li = []
        y_li = []

        for ele in self.points:
            u,v = ele

            if u == px and v-py != 0 :
                x_li.append(ele)
            if v == py and u-px != 0 :
                y_li.append(ele)

        if len(x_li) == 0 or len(y_li) == 0:
            return 0   

        else:
            ans = 0

            for x_match in x_li:
                y_dis = py - x_match[1]

                for y_match in y_li:
                    x_dis = px - y_match[0]

                    if abs(y_dis) == abs(x_dis):                        
                        last_point = (y_match[0],x_match[1])                        

                        if last_point in self.points:
                            # print(self.points[last_point])
                            ans += self.points[last_point] * self.points[tuple(x_match)] * self.points[tuple(y_match)]

        return ans



