class DetectSquares {
    Map<Integer, List<Integer>> mx;
    int[][] points;
    public DetectSquares() {
        mx = new HashMap<>();
        points = new int[1001][1001];
    }
    public void add(int[] point) {
        int x = point[0], y = point[1];
        points[x][y]++;
        if(!mx.containsKey(x)) mx.put(x, new ArrayList<>());
        mx.get(x).add(y);
    }

    public int count(int[] point) {
        int x = point[0], y = point[1];
        if(!mx.containsKey(x)) return(0);
        int res = 0;
        for(int b: mx.get(x)){
            int d = Math.abs(y - b);
            if(d == 0) continue;

            if(x - d >= 0) res += points[x - d][y] * points[x - d][b];
            if(x + d <= 1000) res += points[x + d][y] * points[x + d][b];

        }

        return(res);

    }
}

