func (d *DetectSquares) Count(point []int) int {
	var result int
	x1 := int16(point[0])
	y1 := int16(point[1])
	for y2, count := range d.xy[x1] {
		if y2 == y1 {
			continue
		}
		width := y2 - y1
		if width < 0 {
			width = -width
		}
		x2 := x1 - width
		result += int(count) * int(d.xy[x2][y1]) * int(d.xy[x2][y2])
		x2 = x1 + width
		result += int(count) * int(d.xy[x2][y1]) * int(d.xy[x2][y2])
	}
	return result
}
