class DetectSquares {

    Map<Pair<Integer, Integer>, Integer> pointCounts;

    public DetectSquares() {
        pointCounts = new HashMap<>();
    }

    public void add(int[] point) {

        Pair<Integer, Integer> key = new Pair<>(point[0], point[1]);

        pointCounts.put(key, pointCounts.getOrDefault(key, 0) + 1);
    }

    public int count(int[] point) {

        int count = 0;

        for (Pair<Integer, Integer> pointKey : pointCounts.keySet()) {
            int xDist = Math.abs(point[0] - pointKey.getKey());
            int yDist = Math.abs(point[1] - pointKey.getValue());

            // If it is not diagonal or it is the same point, ignore
            if (xDist != yDist || xDist == 0) continue;

            Pair<Integer, Integer> point1 = new Pair<>(pointKey.getKey(), point[1]);
            Pair<Integer, Integer> point2 = new Pair<>(point[0], pointKey.getValue());

            count += pointCounts.get(pointKey) * pointCounts.getOrDefault(point1, 0) *
                pointCounts.getOrDefault(point2, 0);
        }

        return count;
    }
}

