unordered_map <int,map<int,int>> umap;

    void add(vector<int> point) { umap[point[0]][point[1]]++; }

    int count(vector<int> point) {
        int x = point[0],y = point[1],cnt = 0;

        for(auto iter : umap){
            int dx = iter.first;auto it = iter.second;
            for(auto i : it){
                int dy = i.first;
                if(umap.find(x)==umap.end()){ continue; }
                if(umap.find(dx)==umap.end()){ continue; }
                if(abs(dx-x)!=abs(dy-y)){ continue; }
                if(dx==x || dy==y){ continue; }
                auto map1 = umap[x],map2 = umap[dx];
                if(map1.find(dy)!=map1.end() && map2.find(y)!=map2.end()){
                    cnt+=(map1[dy]*map2[y]*i.second);
                }
            }
        } return cnt;
    }
