    vector<int> missingRolls(vector<int>& rolls, int mean, int n) {
        int sum=accumulate(rolls.begin(),rolls.end(),0);
        int sz=rolls.size();
        int valSum=mean*(sz+n);
        valSum-=sum;
        if(valSum<=0)
            return {};
        if(valSum>(n*6))
            return {};
        int t=valSum/n;
        if(t==0)
            return {};
        vector<int> ans(n,t);
        int k=valSum%n;
        for(int i=0;i<k;i++)
            ans[i]++;
        return ans;
    }
};
