class Solution {
public:
    vector<int> missingRolls(vector<int>& rolls, int& mean, int& n) {
        mean = mean * (n + rolls.size()) - accumulate(rolls.begin(), rolls.end(), 0);
        if (mean < n || mean > n * 6) return {};
        rolls = vector(n, mean / n);
        for(n = mean % n; n > 0; --n) ++rolls[n];
        return rolls;
    }
};
```
