class Solution {
public:
    vector<int> missingRolls(vector<int>& rolls, int mean, int n) {
        int sum=mean*(n+rolls.size());// total sum
        for(auto x:rolls)// substract the current rolls
            sum-=x;
        if(sum<n || sum>n*6) // if not possible to create one
            return {};

        int f=6;// start filling from the largest possible ( Greedy )
        vector<int> res;
        for(int i=n-1;i>=0;i--)
