class Solution {
    public int[] missingRolls(int[] rolls, int mean, int n) {
        //calculate the total sum to get the expected mean
        int expectedSum = mean * (rolls.length + n);
        //calculate the sum of what we already have
        int currentSum = 0;
        for(int roll: rolls) currentSum += roll;
        //lets find whats the target sum, remaining sum to build.
        int targetSum = expectedSum - currentSum;

        //check edge cases
        //1. if the targetSum 3 and n is 4, we will never reach that. dice sides (1 to 6)
        //2. if the targetSum 13 and n is 2, we will never reach that either. dice sides (1 to 6)
        if(targetSum < n || targetSum > n * 6) return new int[]{};

        int[] result = new int[n];
        //finding the each roll
        int div = targetSum/n;
        //what is left?
        int remain = targetSum%n;

        for(int i = 0; i < n; i ++){
            //set the default roll
            result[i] = div;
            //if we are not out of boundary (6) and still have remainer, just keep adding.
            while(remain > 0 && result[i] < 6){
                result[i]++;
                remain--;
            }
        }

        return result;
    }
}
