var missingRolls = function(rolls, mean, n) {
    const m = rolls.length;
    const observations = m + n;

    let totSum = observations * mean;

    for (let i = 0; i < m; ++i) {
        const val = rolls[i];

        if (val > totSum) return [];
        totSum -= val;
    } 

    if (totSum < n) return [];

    const res = [];

    totSum -= n;

    for (let i = 0; i < n; ++i) {
        const addVal = Math.min(5, totSum);
        res[i] = addVal + 1;
        totSum -= addVal;
    }

    return totSum != 0 ? [] : res;
};
