    public int[] missingRolls(int[] rolls, int mean, int n) {
        int sum = 0;
        for (int roll: rolls) sum = sum + roll;
        sum = (n + rolls.length) * mean - sum;
        if (sum<n || (double)sum / n > 6) {
            return new int[] {};
