var missingRolls = function(rolls, mean, n) {
    const sum = rolls.reduce((sum, val) => sum + val, 0)
    const total = mean * (rolls.length + n)
    const remaining = total - sum


    if (remaining < n || remaining > 6 * n)
        return []


