class Solution {
    public int[] missingRolls(int[] r, int mn, int n) {
        int m = r.length;
        int[] ans = new int[n];
        int rs = 0; for(int x : r) rs+=x;
        int s = mn*(n+m) - rs; int sum = s;
        int v = (int) Math.floor(s/n);
        for(int i = 0; i < n; ++i)ans[i] = v;
        s -= v*n; int i = 0;
        while(s > 0 && i < n ){
            int x = 6 - ans[i];
            ans[i] += s > x ? x : s;
            s -= x;
            i++;
        }
        for(int y:ans) if(y < 1 || y > 6) return new int[]{};
        int ns = 0; for(int y : ans) ns+=y;
        return ns == sum ? ans : new int[]{};
    }
}
