class Solution {
    public int[] missingRolls(int[] nums, int mean, int n) {

        int m = nums.length;
        int sum = 0;
        for (int num: nums) {
            sum += num;
        }

        int remaining = mean*(m+n) - sum;
        if (remaining < n) {
            return new int[0];
        }

        int[] ans = new int[n];
        for (int i=1; i<=6 && remaining >0; i++) {
            for (int j=0; j<n && remaining>0; j++) {
                ans[j]++;
                remaining--;
            }
        }

        if (remaining > 0)
            return new int[0];
        else
            return ans;
    }
}
