func missingRolls(rolls []int, mean int, n int) []int {
    m := 0
    length := len(rolls) + n
    // total is what the sum of n + m should be
    total := mean * length

    // get the sum of rolls aka m
    for i := 0; i < len(rolls); i++ {
        m += rolls[i]
    }

    // target is our target sum of n
    target := total - m
    part := target / n
    sum := 0
    ans := make([]int, n)

    // if our fraction is smaller or larger than a die
    // its impossible
    if part < 1 || part > 6 {
        return []int{}
    }

    for i := 0; i < len(ans); i++ {
        sum += part
        ans[i] = part
    }

    i := 0

    // increment dice by 1 until we reach our target n
    for sum < target {
        for i < len(ans) && ans[i] == 6 {
            i++
        }

        if i == len(ans) {
            return []int{}
        }

        ans[i]++
        sum++
    }

    return ans
}
