public class Solution {
    public int[] MissingRolls(int[] rolls, int mean, int n) {
        int[] result = new int[n];
        int sumSoFar = rolls.Sum();
        int totalN = rolls.Length + n;
        int remainingSum = (mean * totalN) - sumSoFar;

        if((remainingSum < n) || (remainingSum > 6*n))
            return new int[0];

        int equalShare = remainingSum/n;
        int remainder = remainingSum%n;
        Array.Fill(result, equalShare);

        for(int i = 0; i < remainder; i++)
            result[i] += 1;

        return result;
    }
}
