class Solution {
public:
    vector<int> missingRolls(vector<int>& rolls, int mean, int n) {
        vector<int> v;
        long long s=0;
        for(int i=0;i<rolls.size();i++){
            s = s + rolls[i];
        }
        long long size = n+rolls.size();
        long long sum = ((mean*size) - s);

       if(sum>6*n || sum<n){
           return {};
       }else
       if(sum%n==0){
           for(int i=0;i<n;i++){
               v.push_back(sum/n);
           }
       }else{
           // if missingSum%n is not zero then after assigning missingSum/nto every element     we will left with missingSum%n.So assign 1 to missingSum%n elements of new array .
           int remaind=sum%n;
            for(int i=0;i<n;i++){
                v.push_back(sum/n);      
            }
            for(int i=0;i<remaind;i++){
                v[i] = v[i] + 1;           
            }
       }

        return v;
    }
};
