class Solution {
    public int[] missingRolls(int[] rolls, int mean, int n) {



        //calculate current total in rolls
        int total = 0;  
        for(int count = 0; count < rolls.length; count++)
            total += rolls[count]; 


        //calculate how much we have to add to the roll's total to get to the target mean 
            //logic: 
                //(total + x)/(rolls.length + n) = mean 
                //then simply isolate for x and use that expression as the number "quantity" to be added to the total
        int leftOver = (mean * (rolls.length + n)) - total; 

        //now we MUST place 1 in every position of subarray to be added (that is, each added dice roll must be at
        // least one so let's account for that fact now by subtracting n from leftOver)
        leftOver -= n; 

        //create a new array of size n to represent the new rolls to be added 
        int result[] = new int[n]; 

        //now start adding the leftOver in the new array adding at max 5 at anyone position and at minimum adding 0
        for(int count = 0; count < result.length; count++) {

            //add the minimum dice roll accounted for earlier at every array position
            result[count] += 1; 

            if(leftOver != 0) {
                //add leftOver's remainder in 5 to the current array position
                //if remainder in 5 == 0 
                if(leftOver % 5 == 0){
                    //then add 5 to the current position 
                    result[count] += 5; 

                    //decrement leftOver by 5 
                    leftOver -= 5; 
                }
                //otherwise remainder is between [1, 4] inclusively 
                else {
                    //in that case we just add the remainder calculated to the current position 
                    result[count] += (leftOver % 5); 

                    //and we decrement leftOver by remainder in 5 
                    leftOver -= (leftOver % 5); 

                }

            }

        }

        //deal with the case in which we weren't able to reduce leftOver to zero 
            //this occurs when the number of added rolls is not sufficient to increase the average to the target 
            //average even when every newly added roll == 6 

        if(leftOver != 0) {
            //return an empty array
            return new int[0];
        }

        return result; 
    }
}
