    // 6 * 10^5 is not overflow with int
    vector<int> missingRolls(vector<int>& rolls, int mean, int n) {
        int sum = accumulate(rolls.begin(), rolls.end(), 0);
        vector<int> ans(n, 1);
        int total = mean * (rolls.size() + n);
        if (total < sum + ans.size()) return {};
        if (total > sum + 6 * ans.size()) return {};
        total -= sum + ans.size();
        for (int i = 0; i < ans.size() && total > 0; ++i){
            total -= 5;
            ans[i] = 6;
            if (total < 0) ans[i] += total, total = 0;
        }
        return ans;
