    def missingRolls(self, rolls: List[int], mean: int, n: int) -> List[int]:

        m = len(rolls)
        sum_m = sum(rolls)
        sum_n = mean*(m+n) - sum_m
        if (sum_n) > n*6 or sum_n<=0:
            return []
        else:
            ans = []
            avg = math.floor(sum_n/n)
            if avg == 0:
                return []
            for i in range(n-1):
                ans.append(avg)
            curr_s = sum(ans)
            rem = sum_n - curr_s

