        if(total+n > (rolls.length+n) * mean){
            return new int[]{};
        }
        // all array times mean 
        // and assuming min 1 for each roll so (total+n)
        int remaining =  ((rolls.length+n) * mean) - (total + n);

        // if there are 3 places left and remaining after putting 1s is 16
        // 16/3 = like 5.12.. so it is not possible to place any way
        if((double) remaining / n > 5.0) return new int[]{};

        // calculated 1's above before even filling the array
        Arrays.fill(res,1);
        int j= 0;

        while(remaining >0){
            int inc = Math.min(5, remaining);
            res[j] +=inc;
            remaining -= inc;
            j++;
        }
        return res;
    }
}
