func missingRolls(rolls []int, mean int, n int) []int {
    requiredSum := mean * (len(rolls) + n) - sum(rolls)
    var requiredMean float64 = float64(requiredSum) / float64(n)

    if requiredMean > 6 || requiredMean < 1 {
        return []int{}
    }

    requiredElements := make([]int, n)

    for i, _ := range requiredElements {
        ceiledMean := int(math.Ceil(requiredMean))
        requiredElements[i] = ceiledMean

        requiredSum -= ceiledMean
        n -= 1
        requiredMean = float64(requiredSum) / float64(n)
    }    

    return requiredElements
}

func sum(rolls []int) (result int) {
    for _, r := range rolls {
        result += r
    }

    return
}
