    public int[] missingRolls(int[] rolls, int mean, int n) {
        int diff = mean * (n + rolls.length);
        for(int i : rolls)
            diff -= i;
        if(diff <= 0)
        return new int[]{};

        System.out.println(diff);

        int avg = 1;
        if(diff > n * 6 || diff < n)
        return new int[]{};

        diff -= n;

        int[] ans = new int[n];

        for(int i = 0; i < n; i++) {
            ans[i] = avg;
            if(diff > 0) {
                int val = Math.min(6 - ans[i], diff);
                diff -= val;
                ans[i] += val;
            }

        }
        return ans;
    }
}
