class Solution {
    // time O(m + n)
    // space O(1)
    public int[] missingRolls(int[] rolls, int mean, int n) {
        int m = rolls.length;
        int rollsSum = 0;
        for (int i = 0; i < m; i++) {
            rollsSum += rolls[i];
        }

        int totalSum = mean * (m + n);
        int nSum = totalSum - rollsSum;
        if (nSum < 0) return new int[]{}; // [-2, -2] case 

        int avg = nSum / n;
        int rest = nSum % n;
        if (avg > 6 || avg < 1) return new int[]{}; // [7, 7] or [0, 0] case
        if (rest > 0 && avg + 1 > 6) return new int[]{}; // [6, 7] case

        int[] res = new int[n];
        for (int i = 0; i < n; i++) {
            res[i] = avg;
            if (rest-- > 0) res[i] += 1;
        }

        return res;
    }
}
