class Solution:
    def missingRolls(self, A: List[int], M: int, F: int) -> List[int]:
        sum_A = 0
        for c in A:
            sum_A += c

        target = int((len(A) + F) * M - sum_A)
        if target == F:
            return [1] * F
        elif (target > F): 
            if (int(1 + (target - F) / F) == 1 + (target - F) / F):
                if 1 + (target - F) / F <= 6:
                    return [int(1 + (target - F) / F)] * F
                else:
                    return []
            else:
                output = [1] * F
                to_add = target - F
                if int(target - F + 1) <= 6:
                    output[-1] = int(target - F + 1)
                    return output
                else:
                    x = int(target - F)
                    index = 0
                    while (x > 5) & (index < F):
                        output[index] += 5
                        x = int(x - 5)
                        index += 1
                    if (x <= 5) and (index < F):
                        output[index] += x
