class Solution {
public:
    vector<int> missingRolls(vector<int>& rolls, int mean, int n) {

        // find the present sum of dice rolls
        int present_roll_sum = accumulate(rolls.begin(), rolls.end(), 0);
        // find the remaining sum of dice rolls
        int rem_sum = mean*(n+rolls.size()) - present_roll_sum;

        // preliminary check. Straight away we get to know
        // if we can't find such a dice roll
        // return an empty array
        if(rem_sum < n or rem_sum > 6*n) return {};

        // fill the rolls with threshold
        // increment the remaining number of elements by 1
        // such that sum satisfies
        int thresh = rem_sum/n;
        vector<int> nrolls(n, thresh);
        for(int i = 0 ; i < rem_sum%n ; i++) nrolls[i]++;

        // return the possible dice roll.
        return nrolls;
    }
};
