func missingRolls(rolls []int, mean int, n int) []int {
	sum := 0
	for _, r := range rolls {
		sum += r
	}

	rest := ((len(rolls)+n) * mean) - sum
	if rest < n || rest > n*6 {
		return []int{}
	}

	res := []int{}
	for n > 0 {
		t := min(rest/n, 6)
		res = append(res, t)
		rest -= t
		n--
	}
	return res
}

