class Solution {
public:
    vector<int> missingRolls(vector<int>& rolls, int mean, int n) {
        int m = rolls.size() , total = 0;
        for(auto x:rolls)
            total+=x;

        int remain = mean*(m+n) - total;

        // chq whether its possible or not
        if(remain > 6 * n || remain < n ) {return {};}

        //now it has to  be possible
        vector<int> ans(n , remain/n);

        remain = remain%n ; // that's carry over left

        // distribute carry over everyone
        // take care if you dont allocate more than 6 number to any array element
        for(int i=0;i<n;i++){
            int cap = 6 - ans[i];
            if(remain<=0) return ans;
            ans[i] += min(cap , remain);
            remain = max(remain - cap , 0 );

        }
        return ans;
    }
};

```

