        int r = sum % n ;
        if (x <= 0 || x > 6 || (x == 6 && r != 0)) return new int[0] ;

        for (int i = 0; i < n; i++) {
            result[i] = x ;
            if (r-- > 0) result[i]++ ;
        }
        return result ;
    }
