class Solution:
    def missingRolls(self, rolls: List[int], mean: int, n: int) -> List[int]:
        desiredCount = len(rolls) + n
        currentSum, targetSum, res = sum(rolls), mean * desiredCount, []
        leftSum = targetSum - currentSum
        leftAverage = leftSum // n
        if leftAverage == 6 and leftAverage * n < leftSum:
            return res
        if leftAverage > 6:
            return res
        if leftAverage <= 0:
            return res
        for i in range(n - 1):
            res.append(leftAverage)
        c = leftSum - (leftAverage * (n - 1))
        if c > 6:
            d = c - leftAverage
            for i in range(1, d):
                res.pop()
            for i in range(1, d + 1):
                res.append(leftAverage + 1)
        else:
            res.append(c)
        return res
