class Solution {
    public int[] missingRolls(int[] rolls, int mean, int n) {
        int sum=0;
        for(int i=0;i<rolls.length;i++){
            sum+=rolls[i];
        }
        int total = (rolls.length+n)*mean - sum;
        if(total<n||total>n*6)
            return new int[0];
        int[] arr = new int[n];
        if(total%n==0)
        {
            int k=total/n;
            for(int i=0;i<n;i++)
                arr[i]=k;
            return arr;
        }
        int j=0;
        for(int i=1;i<=7;i++){
            if(i*n>total){
                int k=total/(i-1);
                if(k>=n&&n*(i-1)!=total)
                 {   k=n-1;
                    while((n-k)*6<(total-k*(i-1))) //check for how much k values can we put in the array
                        k--;
                 }
                for(int m=0;m<k;m++) //add i in the array
                    arr[j+m]=i-1;
                j+=k;
                n-=k;
                total-=(k*(i-1));             //updating total and the value of n to be put after addition

            }
            if(total==0)
                break;
        }
        return arr;
    }
}

