function missingRolls(rolls: number[], mean: number, n: number): number[] {

    let sum = 0;
    for(let r of rolls) {
        sum += r;
    }

    let expectedSum = mean * (rolls.length + n);
    let diff = expectedSum - sum;

    let expectedMean = diff/n;

    if(expectedMean > 6 || expectedMean < 1) {
        return [];
    }

    let result = [];
    let remaining = diff;

    for(let i=0; i<n; i++) {
        let closest = Math.round(remaining/(n-i));
        result.push(closest);
        remaining = remaining - closest;
    }

    return result;
};
