class Solution {
public:
    vector<int> missingRolls(vector<int>& rolls, int mean, int n) {

        int roll_num = rolls.size() + n;
        int total_sum = mean * roll_num;
        int left = total_sum - accumulate(rolls.begin(), rolls.end(), 0);

        if (left < n or n * 6 < left)
            return {};

        int mean_left = left / n;
        vector<int> res(n, mean_left);
        left %= n;
        for (auto& n : res) {
            if (left > 0) {
                auto diff = min(left, 6 - n);
                n += diff;
                left -= diff;
            } else {
                break;
            }
        }
        return res;
    }
};
