class Solution:
    def removeElements(self, head: Optional[ListNode], val: int) -> Optional[ListNode]:
        if head == None:
            return head

        newNext = self.removeElements(head.next, val)
        if head.val == val:
            return newNext
        else:
            head.next = newNext
            return head
