void removeHelper(ListNode *&list, int val) {
	if (list != nullptr) {
		if (list->val == val) {
		    // You can free `list` here if you know how it is allocated.
			list = list->next;
			removeHelper(list, val);
		} else {
			removeHelper(list->next, val);
		}
	}
}

ListNode *removeElements(ListNode *head, int val) {
	removeHelper(head, val);

	return head;
}
