    // curr node points to the head node...
    curr = head;
    // Loop till curr not null...
    while(curr != NULL){
        // If we find the target val same as the value of curr...
        if(curr->val == val){
            // Skip that value and keep updating...
            prev->next = curr->next;
        }
        // Move prev when the next node of prev is not 'val'
        else{
            prev = curr;
        }
        // Move curr forward...
        curr = curr->next;
    }
    return head;
}
