class Solution {
public:
    ListNode* removeElements(ListNode* head, int val, ListNode* tmp = NULL) {
        return !head || (tmp && !tmp->next) ? head : head->val == val ? removeElements(head->next, val) : !tmp ? removeElements(head, val, head) : tmp->next->val == val ? removeElements(head, val, (tmp->next = tmp->next->next, tmp)) :  removeElements(head, val, tmp->next);
    }
};
```

