public class Solution {
    public ListNode RemoveElements(ListNode head, int val) {
        var ptr = head;

        // Loop through data checking to see if the next node equals the target value
        while (ptr != null && ptr.next != null) {            
            // If the next node value is the target value, set the next node
            // to point to the one after it
            if (ptr.next != null && ptr.next.val == val) {
                ptr.next = ptr.next.next;
            } else {
                // Next node doesnt equal target, advance to the next node
                ptr = ptr.next;
            }
        }        
        // If the head equals the target value, advance to the next
        if (head != null && head.val == val) {
            head = head.next;
        }          
        return head;
    }
}
