def removeElements(self, head, val):
    """
    :type head: ListNode
    :type val: int
    :rtype: ListNode
    """
    # Remove the heading nodes
    while head and head.val == val:
        head = head.next
    if not head:
        return head
    prev, cur = head, head.next
    while cur:
        while cur and cur.val == val:
            cur = cur.next
        prev.next = cur
        if cur:
            prev, cur = prev.next, cur.next
    return head

