/**
 * Definition for singly-linked list.
 * function ListNode(val, next) {
 *     this.val = (val===undefined ? 0 : val)
 *     this.next = (next===undefined ? null : next)
 * }
 */
/**
 * @param {ListNode} head
 * @param {number} val
 * @return {ListNode}
 */
var removeElements = function (head, val) {
// Relocate the head, if the head's value equals to val
  while (head) {
    if (head.val === val) {
      head = head.next;
    } else {
      break;
    }
  }
// cur represents head for now
  let cur = head;
// find the nodes should be removed, and jump, jump... 
// Notice that cur only jumps to the next when found different value.
  while (cur && cur.next) {
    let nextNode = cur.next;
    if (nextNode.val === val) {
      cur.next = nextNode.next;
      nextNode = nextNode.next;
      continue;
    }
    cur = cur.next;
    nextNode = nextNode.next;
  }
  return head;
};


