class Solution {
    public ListNode removeElements(ListNode head, int val) {
        if (head != null) {
            // If the starting node is equal to value move the head to the next node until the value is different
            while(head != null && head.val == val){
                head = head.next;
            }
            // Take a pointer to the current head node and check if the value of the next node is equal to the value
            // If it is equal to the value change the next pointer of the current node to the node ahead
            ListNode currentNode = head;
            while (currentNode != null && currentNode.next != null) {
                if (currentNode.next.val == val) {
                    currentNode.next = currentNode.next.next;
                } else {
                    currentNode = currentNode.next;
                }   
            }
        }
        return head;
    }
}
