    public long kthSmallestProduct(int[] nums1, int[] nums2, long k) {
        long lo = (long) -1e10;
        long hi = (long) 1e10;
        while (lo < hi) {
            final long mid = lo + hi + 1 >> 1;
            if (f(nums1, nums2, mid) < k) {
                lo = mid;
            } else {
                hi = mid - 1;
            }
        }
        return lo;
