    private static long f(int[] nums1, int[] nums2, long mid) {
        long count = 0;
        for (int num : nums1) {
            int lo = 0;
            int hi = nums2.length;
            if (num < 0) {
                while (lo < hi) {
                    final int m = lo + hi >>> 1;
                    if ((long) nums2[m] * num >= mid) {
                        lo = m + 1;
                    } else {
                        hi = m;
