                }
                count += nums2.length - lo;
            } else {
                while (lo < hi) {
                    final int m = lo + hi >>> 1;
                    if ((long) nums2[m] * num < mid) {
                        lo = m + 1;
                    } else {
                        hi = m;
                    }
                }
                count += lo;
            }
        }
        return count;
    }
