**Time O((n + m) * log(10  ^ 10))**
```
var kthSmallestProduct = function(nums1, nums2, k) {
    let rProduct = 10 ** 10, lProduct = -rProduct;
    const negativeNums1 = nums1.filter((num) => num < 0);
    const positiveNums1 = nums1.filter((num) => num >= 0);
    const negativeNums2 = nums2.filter((num) => num < 0);
    const positiveNums2 = nums2.filter((num) => num >= 0);
    const negativeNums1Reverse = [...negativeNums1].reverse();
    const positiveNums1Reverse = [...positiveNums1].reverse();
    const negativeNums2Reverse = [...negativeNums2].reverse();
    const positiveNums2Reverse = [...positiveNums2].reverse();

    const findRank = (list1, list2, product) => {
        let rank = 0;

        for (let i = list1.length - 1, j = 0; i >= 0; i--) {
			for (; j < list2.length && list2[j] * list1[i] <= product; j++);
            rank += j;
        }

        return rank;
    };

    while (lProduct < rProduct) {
        const midProduct = Math.floor((lProduct + rProduct) / 2);

        if (midProduct >= 0) {
            var rank = negativeNums1.length * positiveNums2.length +
                negativeNums2.length * positiveNums1.length +
                findRank(negativeNums1Reverse, negativeNums2Reverse, midProduct) +
                findRank(positiveNums1, positiveNums2, midProduct)
        } else {
            var rank = findRank(positiveNums1Reverse, negativeNums2, midProduct) +
                findRank(positiveNums2Reverse, negativeNums1, midProduct);
        }

        rank < k ? (lProduct = midProduct + 1) : (rProduct = midProduct);
    }

    return lProduct;
};
