Solution is C# version of [this ](https://leetcode.com/problems/kth-smallest-product-of-two-sorted-arrays/discuss/1524349/Java-Double-Binary-Search-O(log1010-x-MlogN))
```
public class Solution 
{
    static long INF = (long) 1e10;

    public long KthSmallestProduct(int[] nums1, int[] nums2, long k) 
    {
        int m = nums1.Length, n = nums2.Length;
		long lo = -INF - 1, hi = INF + 1;

		while (lo < hi)
		{
			long mid = lo + ((hi - lo) >> 1), cnt = 0;

            foreach (int i in nums1)
			{
				if (0 <= i)
				{
					int l = 0, r = n - 1, p = 0;
					while (l <= r)
					{
						int c = l + ((r - l) >> 1);
						long mul = i * (long)nums2[c];
						if (mul <= mid)
						{
							p = c + 1;
							l = c + 1;
						}
						else
						{
							r = c - 1;
						}
					}
					cnt += p;
				}
				else
				{
					int l = 0, r = n - 1, p = 0;
					while (l <= r)
					{
						int c = l + ((r - l) >> 1);
						long mul = i * (long)nums2[c];
						if (mul <= mid)
						{
							p = n - c;
							r = c - 1;
						}
						else
						{
							l = c + 1;
						}
					}
					cnt += p;
				}
			}
			if (cnt >= k)
			{
				hi = mid;
			}
			else
			{
				lo = mid + 1L;
			}
		}
		return lo;

    }
}
